      VF2_2 = ifelse(`Você já se envolveu em ativismo ambiental? (Por exemplo, Todos Pela Amazônia, se recusa a comprar produtos específicos, protestos, etc.)` == 'Sim', 1, 0),
    
      VF3_1 = ifelse(`Como você se locomove no dia a dia? (por exemplo, para ir à escola/faculdade/trabalho, ir ao supermercado, visitar amigos)` == 'Na maioria das vezes, ando a pé ou de bicicleta', 1, 0),
