      VF6_2 = ifelse(`Você come peixe?` == 'Sim', 1, 0),
    
      VF7_1 = ifelse(`Você come ovos ou laticínios (por exemplo, leite, queijo, iogurte)?` == 'Não', 1, 0),
      VF7_2 = ifelse(`Você come ovos ou laticínios (por exemplo, leite, queijo, iogurte)?` == 'Sim', 1, 0),
      
